        position: absolute;
        display: grid;
        place-items: center;
        height: 100%;
        bottom: -10px;
        opacity: 0;
        transition: $transition_default;

        @media(max-width:$br_mobile) {
            bottom: 0px;
            opacity: 1;
        }

        svg {
            path {
                fill: var(--text_color);
                &:last-child{
                    fill: var(--primary);

                }
            }
        }

    }

    &:hover {
        strong {
            bottom: 0px;
            opacity: 1;
        }
    }
  }
</style>